package project;

public class T0730 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b, sum;
		a = Integer.parseInt(args[0]);
		b = Integer.parseInt(args[1]);
		sum = a + b;
		System.out.println("두수의 합은" + sum + "입니다");
		sum = a - b;
		System.out.println("두수의 합은" + sum + "입니다");
		sum = a * b;
		System.out.println("두수의 합은" + sum + "입니다");
		sum = a / b;
		System.out.println("두수의 합은" + sum + "입니다");
	}

}
